SELECT

FROM
	t_application
WHERE
     1 = 1

		and del_state = #{entity.delState, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


		and class_type = #{entity.classType, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


		and app_status = #{entity.appStatus, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


		and supplier_id = #{entity.supplierId}



	order by trade_count desc


		LIMIT #{entity.size}

SELECT

FROM
	t_application
WHERE

	del_state = #{entity.delState, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


	and app_status = #{entity.appStatus, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


	and class_type = #{entity.classType, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


	and supplier_id = #{entity.supplierId}






		and app_name = #{entity.queryName}
		and supplier_id in
		(
			select id from t_supplier where supplierl_name like CONCAT('%',#{entity.queryName},'%')
		)




		and app_name like CONCAT('%',#{entity.queryName},'%')
		and class_type = #{entity.queryType, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


order by id desc

