mapper.select:
SELECT

FROM
	t_application_request


		del_state = #{entity.delState, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}




			and user_id in

				#{item}



			and id = #{entity.queryName}


			and app_name like CONCAT('%',#{entity.queryName},'%')



			and (
			user_id in

				#{item}

			or id = #{entity.queryName}
			or app_name like CONCAT('%',#{entity.queryName},'%')
			)





		and
			app_id in
			(
			select
				id from t_application
			where
				supplier_id in

				#{item}

			)




		and create_by = #{entity.loginName}

SELECT

FROM
	t_application_request


		del_state = #{entity.delState, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


		and id = #{entity.id}
