mapper.select:
SELECT

FROM
	t_order


		del_state = #{entity.delState, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


		and status = #{entity.status, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


		and create_time >= #{entity.createTimeSta}


		and create_time <= #{entity.createTimeEnd}




			and order_no = #{entity.queryName}


			and id in
				(select order_id from t_order_detailed where app_name = #{entity.queryName})


			and (
				order_no = #{entity.queryName}
			or id in
				(select order_id from t_order_detailed where app_name = #{entity.queryName})
			)

SELECT

FROM
	t_order


		id = #{entity.id}


		and del_state = #{entity.delState, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}
