mapper.select:
SELECT

FROM
	t_supplier_request


		del_state = #{entity.delState, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}




			and user_name like CONCAT('%',#{entity.queryName},'%')


			and id = #{entity.queryName}


			and supplier_name like CONCAT('%',#{entity.queryName},'%')



			and (
			user_name like CONCAT('%',#{entity.queryName},'%')
			or id = #{entity.queryName}
			or supplier_name like CONCAT('%',#{entity.queryName},'%')
			)

SELECT

FROM
	t_supplier_request


		del_state = #{entity.delState, typeHandler=com.camelot.kuka.common.mybatis.handler.EnumTypeHandler}


		and id = #{entity.id}
